package de.themorpheus.edu.gateway.graphql.resolver.query.task;

import de.themorpheus.edu.backend.api.SubjectApi;
import de.themorpheus.edu.backend.invoker.ApiException;
import de.themorpheus.edu.backend.model.Subject;
import de.themorpheus.edu.gateway.graphql.dto.task.SubjectDTO;
import graphql.schema.DataFetchingEnvironment;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.stereotype.Component;
import javax.validation.constraints.Min;
import graphql.kickstart.tools.GraphQLQueryResolver;

@Component
public class SubjectResolver implements GraphQLQueryResolver {

	@Autowired private SubjectApi subjectApi;

	public SubjectDTO subjectById(@Min(0) int subjectId, DataFetchingEnvironment environment) throws ApiException {
		Subject subject = subjectApi.getSubjectbysubjectId("SECRET", subjectId, "eyJhbGciOiJIUzUxMiIsInR5cCI6IkpXVCJ9..m3MuV-N7lkYn3SSamesEZ1o6FMrrMNQBXMv8JneSuoXu-5KgN2p7VMZJlvXN0_toKQQ1wixlGh-DxSO6pEfU_w");
		return new SubjectDTO(
			subject.getSubjectId(),
			subject.getNameKey()
		);
	}

}
